/* eslint-disable react/no-array-index-key */
/* eslint-disable padded-blocks */
/* eslint-disable react/self-closing-comp */
/* eslint-disable react/prop-types */
/* eslint-disable react/react-in-jsx-scope */
import GoogleMapReact from 'google-map-react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchVehiclesLocations } from '../../state/actions/mapActions';
import Loader from '../Loader';
import handleApiLoaded, { defualtPolygonCoordinates } from './polygon';
import { getLocationMarkers } from './mapUtils';
import usePolygon from './usePolygon';

const Map = ({ center, zoom }) => {
    const dispatch = useDispatch();
  
    const { loading, vehiclesLocations, selectingArea } = useSelector((state) => state.map);
    const [coordinates, setCoordinates] = useState([]);
    useEffect(() => {
      setCoordinates(selectingArea ? defualtPolygonCoordinates : []);
    }, [selectingArea]); 
    useEffect(() => dispatch(fetchVehiclesLocations()), []);

    const locationMarkers = getLocationMarkers(vehiclesLocations);

    return (
      <div className="map">
        {loading 
          ? <Loader />
          : (
            <GoogleMapReact 
              bootstrapURLKeys={{ key: process.env.REACT_APP_API_KEY }}
              defaultCenter={center}
              defaultZoom={zoom}
              yesIWantToUseGoogleMapApiInternals 
              onGoogleApiLoaded={({ map, maps }) => handleApiLoaded(map, maps, coordinates)}
            >
              {locationMarkers}
            </GoogleMapReact>
        )}
      </div>
    );
};

Map.defaultProps = {
    center: {
        lat: 51.509865,
        lng: -0.118092,
    },
    zoom: 11,
};

export default Map;
