import React from 'react';
import { fetchVehicleIds } from '../../state/actions/mapActions';
import { store } from '../../state/store';

const defualtPolygonCoordinates = [
  {   
    lat: 51.522121898364986,
    lng: -0.1593582845911126,
  },
  {  
    lat: 51.489486554202024,
    lng: -0.16009828252122368,
  },
  { 
    lat: 51.48945352696307,
    lng: -0.09697481847935624,
  },
  {   
    lat: 51.52190825390342,
    lng: -0.09600745825457122,
  },
];

const handlePolygonMouseUp = (polygon) => {
  const vertices = polygon.getPath();
  const coordinates = [];
  for (let i = 0; i < vertices.getLength(); i++) {
    const xy = vertices.getAt(i);
    coordinates.push([xy.lat(), xy.lng()]);
  }
  store.dispatch(fetchVehicleIds(coordinates));
};

const usePolygonApi = () => {
  const handleApiLoaded = (map, maps, coordinates) => {
    const polygon = new maps.Polygon({
      paths: coordinates,
      strokeColor: '#FF0000',
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: '#FF0000',
      fillOpacity: 0.35,
      editable: true,
      draggable: true,
      geodesic: true,
      setVisi
    });
    
    polygon.setMap(map);
    polygon.addListener('mouseup', () => handlePolygonMouseUp(polygon));
  };

  return [handleApiLoaded, defualtPolygonCoordinates];
};

export default usePolygonApi;

