/* eslint-disable no-nested-ternary */
/* eslint-disable max-len */
/* eslint-disable no-sparse-arrays */
/* eslint-disable react/no-array-index-key */
/* eslint-disable padded-blocks */
/* eslint-disable react/self-closing-comp */
/* eslint-disable react/prop-types */
/* eslint-disable react/react-in-jsx-scope */
import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchVehiclesLocations } from '../../state/actions/mapActions';
import Loader from './Loader';
import { generateLocationMarkers } from './mapUtils';
import GoogleMapWithPolygon from './GoogleMapWithPolygon';
import GoogleMapWithoutPolygon from './GoogleMapWithoutPolygon';

const mapConfig = {
  center: {
      lat: 51.509865,
      lng: -0.118092,
  },
  zoom: 11,
};

const Map = () => {
    console.log('RENDER - map');
    const mapRef = useRef();
    const [zoom, setZoom] = useState(10);
    const [bounds, setBounds] = useState(null);
    const dispatch = useDispatch();
    const { loading, vehiclesLocations, selectingArea } = useSelector((state) => state.map);

    const locationMarkers = generateLocationMarkers(vehiclesLocations);

    useEffect(() => dispatch(fetchVehiclesLocations()), []);

    const renderProperMap = () => {
      return selectingArea 
            ? <GoogleMapWithPolygon mapConfig={mapConfig} locationMarkers={locationMarkers} />
            : <GoogleMapWithoutPolygon mapConfig={mapConfig} locationMarkers={locationMarkers} />; 
    };
    
    return (
      <div className="map">
        {loading ? <Loader /> : renderProperMap()}
      </div>
    );
};

export default Map;
