/* eslint-disable no-plusplus */
import React from 'react';

const handlePolygonMouseUp = (polygon) => {
  const vertices = polygon.getPath();
  const polygonData = [];
  for (let i = 0; i < vertices.getLength(); i++) {
    const xy = vertices.getAt(i);
    polygonData.push([xy.lat(), xy.lng()]);
  }
  // dispatch polygon coordinants
  console.log(polygonData);
};

const handleApiLoaded = (map, maps, polygonCoordinates) => {
     const polygon = new maps.Polygon({
      paths: polygonCoordinates,
      strokeColor: '#FF0000',
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: '#FF0000',
      fillOpacity: 0.35,
      editable: true,
      draggable: true,
      geodesic: true,
    });
    polygon.setMap(map);
    polygon.addListener('mouseup', () => handlePolygonMouseUp(polygon));
};

export default handleApiLoaded;

// lat: 51.509865,
// lng: -0.118092,
