let infoWindow;

function showArrays(event, map, maps) {
  // Since this polygon has only one path, we can call getPath() to return the
  // MVCArray of LatLngs.
  const polygon = this;
  const vertices = polygon.getPath();
  let contentString = `${'<b>Bermuda Triangle polygon</b><br>'}<br>`;

  // Iterate over the vertices.
  for (let i = 0; i < vertices.getLength(); i++) {
    const xy = vertices.getAt(i);

    contentString
      += `${'<br>' + 'Coordinate '}${i}:<br>${xy.lat()},${xy.lng()}`;
  }

  // Replace the info window's content and position.
  infoWindow.setContent(contentString);
  infoWindow.setPosition(event.latLng);
  infoWindow.open(map);
}

const handleApiLoaded = (map, maps, polygonCoordinates) => {
     const bermudaTriangle = new maps.Polygon({
      paths: polygonCoordinates,
      strokeColor: '#FF0000',
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: '#FF0000',
      fillOpacity: 0.35,
      editable: true,
      draggable: true,
      geodesic: true,
    });
    bermudaTriangle.setMap(map);
    // Add a listener for the click event.
    bermudaTriangle.addListener('click', (event) => showArrays(event, map, maps));
    infoWindow = new maps.InfoWindow();
  };

module.exports = handleApiLoaded;

// lat: 51.509865,
