/* eslint-disable padded-blocks */
/* eslint-disable react/self-closing-comp */
/* eslint-disable react/prop-types */
/* eslint-disable react/react-in-jsx-scope */
import GoogleMapReact from 'google-map-react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import LocationMarker from './LocationMarker';
import { fetchVehiclesLocations } from '../../state/actions/vehicleActions';
import Loader from '../Loader';
import handleApiLoaded from './polygon';

const Map = ({ center, zoom }) => {
    const { loading, vehiclesLocations } = useSelector((state) => state.vehicles);
    const [polygonCoordinates, setPolygonCoordinates] = useState()
    const dispatch = useDispatch();

    useEffect(() => dispatch(fetchVehiclesLocations()), []);

    const vehicleMarkers = vehiclesLocations.map((vehicle, index) => {
        if (index < 10) {
          return <LocationMarker lat={vehicle.lat} lng={vehicle.lng} />;
        } return null;
      });

    return (
      <div className="map">
        {loading 
          ? <Loader />
          : (
            <GoogleMapReact 
              bootstrapURLKeys={{ key: process.env.REACT_APP_API_KEY }}
              defaultCenter={center}
              defaultZoom={zoom}
              yesIWantToUseGoogleMapApiInternals 
              onGoogleApiLoaded={({ map, maps }) => handleApiLoaded(map, maps)}
            >
              {vehicleMarkers}
            </GoogleMapReact>
        )}
      </div>
    );
};

Map.defaultProps = {
    center: {
        lat: 51.509865,
        lng: -0.118092,
    },
    zoom: 11,
};

export default Map;
