/* eslint-disable react/no-array-index-key */
/* eslint-disable padded-blocks */
/* eslint-disable react/self-closing-comp */
/* eslint-disable react/prop-types */
/* eslint-disable react/react-in-jsx-scope */
import GoogleMapReact from 'google-map-react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchVehiclesLocations } from '../../state/actions/mapActions';
import Loader from '../Loader';
import usePolygonApi, { defualtPolygonCoordinates, handlePolygonMouseUp } from './usePolygonApi';
import { getLocationMarkers } from './mapUtils';

const Map = ({ center, zoom }) => {
    console.log('RENDER - map');
    const dispatch = useDispatch();
  
    const { loading, vehiclesLocations, selectingArea } = useSelector((state) => state.map);
    const [coordinates, setCoordinates] = useState([]);
    // const handleApiLoaded = usePolygonApi(coordinates);

    const handleApiLoaded = (map, maps) => {
      const polygon = new maps.Polygon({
        paths: coordinates,
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        editable: true,
        draggable: true,
        geodesic: true,
      });
      
      polygon.setMap(map);
      polygon.addListener('mouseup', () => handlePolygonMouseUp(polygon));
    };

    useEffect(() => setCoordinates(selectingArea
       ? defualtPolygonCoordinates 
       : []),
        [selectingArea]); 

    useEffect(() => dispatch(fetchVehiclesLocations()), []);

    const locationMarkers = getLocationMarkers(vehiclesLocations);

    return (
      <div className="map">
        {loading 
          ? <Loader />
          : (
            <GoogleMapReact 
              bootstrapURLKeys={{ key: process.env.REACT_APP_API_KEY }}
              defaultCenter={center}
              defaultZoom={zoom}
              yesIWantToUseGoogleMapApiInternals 
              onGoogleApiLoaded={({ map, maps }) => handleApiLoaded(map, maps)}
            >
              {locationMarkers}
            </GoogleMapReact>
        )}
      </div>
    );
};

Map.defaultProps = {
    center: {
        lat: 51.509865,
        lng: -0.118092,
    },
    zoom: 11,
};

export default Map;
