/* eslint-disable padded-blocks */
/* eslint-disable react/self-closing-comp */
/* eslint-disable react/prop-types */
/* eslint-disable react/react-in-jsx-scope */
import GoogleMapReact from 'google-map-react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import LocationMarker from './LocationMarker';
import { fetchVehiclesLocations } from '../../state/actions/vehicleActions';

const Map = ({ center, zoom }) => {
    const { loading, vehiclesLocations } = useSelector((state) => state.vehicles);
    const dispatch = useDispatch();

    useEffect(() => dispatch(fetchVehiclesLocations()), []);

    const createVehicleMarkers = () => {
      return vehiclesLocations.map((vehicle, index) => {
        if (index < 10) {
          return <LocationMarker lat={vehicle.lat} lng={vehicle.lng} />;
        } 
      });
    };

    return (
      <div className="map">
        <GoogleMapReact 
          bootstrapURLKeys={{ key: process.env.REACT_APP_API_KEY }}
          defaultCenter={center}
          defaultZoom={zoom}
        >
          {createVehicleMarkers()}
        </GoogleMapReact>
      </div>
    );
};

Map.defaultProps = {
    center: {
        lat: 51.509865,
        lng: -0.118092,
    },
    zoom: 11,
};

export default Map;
