/* eslint-disable no-plusplus */
import React from 'react';

let infoWindow;

function showArrays(event, map, maps, polygon) {
  // Since this polygon has only one path, we can call getPath() to return the
  // MVCArray of LatLngs.
  // console.log(polygon.getPath());
  const vertices = polygon.getPath();
  // const contentString = `${'<b>Bermuda Triangle polygon</b><br>'}<br>`;

  // // Iterate over the vertices.
  for (let i = 0; i < vertices.getLength(); i++) {
    const xy = vertices.getAt(i);
    console.log(`Coordinate ${i}:${xy.lat()},${xy.lng()}`);
  }

  // // Replace the info window's content and position.
  // infoWindow.setContent(contentString);
  // infoWindow.setPosition(event.latLng);
  // infoWindow.open(map);
}

function handleApiLoaded(map, maps, polygonCoordinates) {
     const polygon = new maps.Polygon({
      paths: polygonCoordinates,
      strokeColor: '#FF0000',
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: '#FF0000',
      fillOpacity: 0.35,
      editable: true,
      draggable: true,
      geodesic: true,
    });
    polygon.setMap(map);
    // Add a listener for the click event.
    polygon.addListener('dragend', (event) => showArrays(event, map, maps, polygon));
    // polygon.addListener('click', (event) => console.log(event.latLng.lat()));
    infoWindow = new maps.InfoWindow();
}

export default handleApiLoaded;

// lat: 51.509865,
// lng: -0.118092,
