/* eslint-disable react/no-array-index-key */
/* eslint-disable react/button-has-type */
/* eslint-disable react/self-closing-comp */
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { mapActions } from '../state/slices/mapSlice';

const Dashboard = () => {
    const { selectedVehiclesIds, selectingArea } = useSelector((state) => state.map.selectedVehiclesIds);
    const [btnText, setbtnText] = useState('Select Area');
    const dispatch = useDispatch();
    const handleClick = () => {
      dispatch(mapActions.initSelectingArea());
      setbtnText('Clear Selection Area');
    };

    return (
      <div className="dashboard">
        <div className="dashboard-header">
          <h4>Vehicles in selected area:</h4>
          <button onClick={handleClick}>{btnText}</button>
        </div>
        <div className="ids-list">
          {selectedVehiclesIds.map((id, index) => <p key={index}>{id}</p>)}
        </div>
      </div>
    );
};

export default Dashboard;
