}

const handleApiLoaded = (map, maps, polygonCoordinates) => {
     const bermudaTriangle = new maps.Polygon({
      paths: polygonCoordinates,
      strokeColor: '#FF0000',
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: '#FF0000',
      fillOpacity: 0.35,
      editable: true,
      draggable: true,
      geodesic: true,
    });
    bermudaTriangle.setMap(map);
    // Add a listener for the click event.
    bermudaTriangle.addListener('click', (event) => showArrays(event, map, maps));
    bermudaTriangle.addListener('change', (event) => console.log(event));
    infoWindow = new maps.InfoWindow();
  };

module.exports = handleApiLoaded;

