/* eslint-disable no-plusplus */
import GoogleMapReact from 'google-map-react';
import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getLocationMarkers } from './mapUtils';
import { defualtPolygonCoordinates } from './polygonAPI';
import { fetchVehicleIds } from '../../state/actions/mapActions';

const mapConfig = {
    center: {
        lat: 51.509865,
        lng: -0.118092,
    },
    zoom: 11,
};

const GoogleMap = () => {
    const dispatch = useDispatch();
    const { vehiclesLocations, selectingArea } = useSelector((state) => state.map);
    const locationMarkers = getLocationMarkers(vehiclesLocations);
    console.log('RENDER - google map', selectingArea);

    const [polygonConfig, setPolygonConfig] = useState({
        paths: defualtPolygonCoordinates,
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        editable: true,
        draggable: true,
        geodesic: true,
        visible: selectingArea,
    });

    useEffect(() => {
        setPolygonConfig({ ...polygonConfig, visible: selectingArea });
    }, [selectingArea]);

    const handlePolygonMouseUp = (polygon) => {
        const vertices = polygon.getPath();
        const coordinates = [];
        for (let i = 0; i < vertices.getLength(); i++) {
          const xy = vertices.getAt(i);
          coordinates.push([xy.lat(), xy.lng()]);
        }
        dispatch(fetchVehicleIds(coordinates));
      };
      
      const handleApiLoaded = (map, maps) => {
        console.log('here');
        const polygon = new maps.Polygon(polygonConfig);
        polygon.setMap(map);
        polygon.addListener('mouseup', () => handlePolygonMouseUp(polygon));
      };

    return (
      <GoogleMapReact 
        bootstrapURLKeys={{ key: process.env.REACT_APP_API_KEY }}
        defaultCenter={mapConfig.center}
        defaultZoom={mapConfig.zoom}
        yesIWantToUseGoogleMapApiInternals 
        onGoogleApiLoaded={({ map, maps }) => handleApiLoaded(map, maps)}
      >
        {locationMarkers}
      </GoogleMapReact>
    );
};

export default GoogleMap;
