import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    vehiclesLocations: [],
    selectedVehiclesIds: [],
    loading: true,
};

const vehiclesSlice = createSlice({
    name: 'vehicles',
    initialState,
    reducers: {
        getVehiclesLocations(state, action) {
            state.vehiclesLocations = action.payload;
            state.loading = false;
        },
        getSelectedVehiclesIds(state, action) {
            state.vehicles.selectedVehiclesIds = action.payload;
        },

    },
});

export const vehiclesActions = vehiclesSlice.actions;

export default vehiclesSlice.reducer;
