/* eslint-disable react/jsx-no-comment-textnodes */
import React from 'react';
import { Icon } from '@iconify/react';
import Badge from 'react-bootstrap/Badge';
// eslint-disable-next-line import/no-unresolved

const LocationMarker = ({ count, points }) => {
    const clusterMarkerStyle = {
      width: `${10 + (count / points.length) * 5000}px`,
      height: `${10 + (count / points.length) * 5000}px`,
    };
    return (
      <div className="location-marker">
        {!count 
        ? <Icon icon="ant-design:car-filled" className="location-icon" />
        : (
          <div
            className="cluster-marker"
            style={clusterMarkerStyle}
          >
            {count}
          </div>
)}
        {/* <Badge pill bg="secondary">{count}</Badge> */}
      </div>
    );
};

export default LocationMarker;
