/* eslint-disable no-plusplus */
import React, { useState } from 'react';

let infoWindow;

const showArrays = (polygon) => {
  const vertices = polygon.getPath();

  for (let i = 0; i < vertices.getLength(); i++) {
    const xy = vertices.getAt(i);
    console.log(`${xy.lat()} , ${xy.lng()}`);
  }
};

const handleApiLoaded = (map, maps, polygonCoordinates) => {
     const polygon = new maps.Polygon({
      paths: polygonCoordinates,
      strokeColor: '#FF0000',
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: '#FF0000',
      fillOpacity: 0.35,
      editable: true,
      draggable: true,
      geodesic: true,
    });
    polygon.setMap(map);
    // Add a listener for the click event.
    polygon.addListener('click', () => showArrays(polygon));
    // polygon.addListener('click', (event) => console.log(event.latLng.lat()));
    infoWindow = new maps.InfoWindow();
};

export default handleApiLoaded;

// lat: 51.509865,
// lng: -0.118092,
